import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  data: [],
  status: "idle",
};

export const fetchweather = createAsyncThunk("weather/get", async (datas) => {
  try {
    if (!datas) {
      const sdata = await axios.get(
        "https://api.openweathermap.org/data/2.5/weather?q=Kozhikode&appid=1124ead6a7afed5b553176f1442dfc9c"
      );
      return sdata;
    } else {
      const data = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${datas}&appid=1124ead6a7afed5b553176f1442dfc9c`
      );
      return sdata;
    }
  } catch (error) {
    console.log(error);
  }
});

export const weatherSlice = createSlice({
  name: "weatherapp",
  initialState,
  extraReducers: (builder) => {
    builder
      .addCase(fetchweather.pending, (state, action) => {
        state.status = "loading";
      })
      .addCase(fetchweather.fulfilled, (state, action) => {
        state.data = action.payload;
        state.status = "idle";
      })
      .addCase(fetchweather.rejected, (state, action) => {
        state.status = "error";
      });
  },
});

export default weatherSlice.reducer;
